document.querySelectorAll('.file input[type="file"]').forEach(e=>{e.addEventListener("change",()=>{const t=e.closest("label").nextElementSibling;e.files.length>0?t.textContent="\u0412\u044B \u0432\u044B\u0431\u0440\u0430\u043B\u0438: "+e.files[0].name:t.textContent=""})});const input=document.querySelector(".search__input"),cancelBtn=document.querySelector(".search__button--cancel");input.addEventListener("input",()=>{input.value.trim()!==""?cancelBtn.classList.add("active-clear"):cancelBtn.classList.remove("active-clear")}),cancelBtn.addEventListener("click",()=>{input.value="",cancelBtn.classList.remove("active-clear"),input.focus()});
